mapper.insert:

insert into tb_image (id, user_id, url, 
  path, content_type, original_name, 
  state, category, create_time, update_time
  )
values (#{id,jdbcType=VARCHAR}, #{userId,jdbcType=VARCHAR}, #{url,jdbcType=VARCHAR}, 
  #{path,jdbcType=VARCHAR}, #{contentType,jdbcType=VARCHAR}, #{originalName,jdbcType=VARCHAR}, 
  #{state,jdbcType=VARCHAR},#{category,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP},
  #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.select:

select id, user_id, url, path, content_type, original_name, state, category, create_time, update_time
from tb_image
where id = #{id,jdbcType=VARCHAR}

select id, user_id, url, path, content_type, original_name, state, category, create_time, update_time from tb_image
state = '1'
and category = #{category,jdbcType=VARCHAR}

mapper.update:
update tb_image
set state = '0'
where id = #{imageId,jdbcType=VARCHAR}
